from abstra.forms import Button, TextInput, run


def approval_workflow(state):
    # Check which button was pressed
    if state.get("approve"):
        return [
            TextInput("Approved! Add your approval comments:", key="approval_comments")
        ]
    elif state.get("reject"):
        return [
            TextInput(
                "Rejected. Please provide rejection reason:", key="rejection_reason"
            )
        ]
    else:
        # Initial form with decision buttons
        return [TextInput("Enter request details:", key="request_details")], [
            Button("Approve", key="approve"),
            Button("Reject", key="reject"),
        ]


# Run the workflow
result = run([approval_workflow])

# Print the result
print(result)

from abstra.forms import Button, DropdownInput, TextInput, run


def multi_path_form(state):
    # Check which path the user chose
    if state.get("personal_info"):
        return [
            TextInput("Full Name:", key="name"),
            TextInput("Email:", key="email"),
            TextInput("Phone:", key="phone"),
        ]
    elif state.get("company_info"):
        return [
            TextInput("Company Name:", key="company"),
            TextInput("Job Title:", key="title"),
            DropdownInput(
                "Industry:", ["Tech", "Finance", "Healthcare", "Other"], key="industry"
            ),
        ]
    else:
        # Initial choice form
        return [
            TextInput(
                "Welcome! Please choose what information to provide:", key="welcome"
            )
        ], [
            Button("Personal Info", key="personal_info"),
            Button("Company Info", key="company_info"),
        ]


# Run the form
result = run([multi_path_form])

# Print the result
print(result)

from abstra.forms import EmailInput, TextInput, run


def step_1(state):
    return [
        TextInput("Enter your first name:", key="first_name"),
        TextInput("Enter your last name:", key="last_name"),
    ]


def step_2(state):
    return [
        EmailInput("Enter your email:", key="email"),
        TextInput("Enter your phone number:", key="phone"),
    ]


def step_3(state):
    return [
        TextInput(
            f"Thank you {state.get('first_name', '')}! Any additional comments?",
            key="comments",
        )
    ]


# Run multi-step form - NextButton appears automatically on each step
result = run([step_1, step_2, step_3])

# Print the result
print(result)

from abstra.forms import CustomInput, run

html = """
<button id='date-btn'>Get current date</button>
"""

js = """
document.getElementById('date-btn').addEventListener('click',function() {
        const date = new Date();
        const day = date.getDate();
        const month = date.getMonth() + 1;
        const year = date.getFullYear();

        changeEvent(day + '/' + month + '/' + year);
    });
"""

css = """
body {
        margin: 0;
        padding: 0;
    }

    #date-btn {
        cursor: pointer;
        background-color: #343b46;
        border: none;
        border-radius: 4px;
        color: white;
        padding: 15px 32px;
        text-align: center;
        text-decoration: none;
        display: inline-block;
        font-size: 16px;
    }

    #date-btn:hover {
        background-color: #3e4756;
    }
"""


# Create a page with the widget
example_page = [
    CustomInput(
        html_body=html,
        js=js,
        css=css,
        key="my-custom-input",
    ),
]

# Run the form
result = run([example_page])

# Print the result
print(result)

